import { useEffect, useState } from "react";
import { getData } from "../apis/main";
import Swal from "sweetalert2";
import Loader from "../pages/Loader";
import { Card, Col } from "react-bootstrap";

export default function Details() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [show, setShow] = useState({ status: false, id: 0 });

  useEffect(() => {
    handleData();
  }, []);

  const handleData = async () => {
    setLoading(true);
    const { code, response, error } = await getData();
    switch (code) {
      case 200:
        setData(response.data);
        break;
      default:
        Swal.fire({
          title: error.message,
          icon: "error",
          showCancelButton: false,
          showCloseButton: false,
          showDenyButton: false,
          showConfirmButton: false,
          timer: 2000,
        });
        break;
    }
    setLoading(false);
  };

  if (loading) {
    return <Loader />;
  }

  return data.length < 1 ? (
    <span>Sin inmuebles que mostrar</span>
  ) : (
    data.map(({ id, nombre, descripcion }) => {
      return (
        <Col key={id}>
          <Card
            style={{ cursor: "pointer", width: "18rem" }}
            onClick={() => {
              setShow({ status: show.id === id ? !show.status : true, id });
            }}
          >
            <Card.Body>
              <Card.Title>{nombre}</Card.Title>
              <Card.Img></Card.Img>
              <Card.Text
                className={show.status && id === show.id ? "" : "d-none"}
              >
                {descripcion}
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      );
    })
  );
}
