import { Container, Form, Row } from "react-bootstrap";
import { useState } from "react";
import BtnLogin from "./BtnLogin";

export default function FormLogin() {
  const [data, setData] = useState({ user: "", password: "" });
  const [err, setErr] = useState({ status: false, text: "" });

  const handleChange = (e) => {
    setData((old) => ({ ...old, [e.target.name]: e.target.value }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const { user, password } = data;
    if (
      user === undefined ||
      typeof user !== "string" ||
      user.trim() === "" ||
      password === undefined ||
      typeof password !== "string" ||
      password.trim() === ""
    ) {
      setErr({
        status: true,
        text: "Tiene que completar usuario y contraseña para avanzar",
      });
      return;
    }
  };

  return (
    <Container className="mt-4">
      <span className="my-2">Ingrese sus datos para ingresar</span>
      <Form>
        <Row className="w-50 my-2">
          <Form.Control
            onChange={handleChange}
            type="text"
            placeholder="Usuario"
            name="user"
          />
        </Row>
        <Row className="w-50 my-2">
          <Form.Control
            onChange={handleChange}
            type="password"
            placeholder="Contraseña"
            name="password"
          />
        </Row>
        <BtnLogin onClick={onSubmit} text="Ingresar" type="outline-success" />
        {err.status ? <span>Error</span> : null}
      </Form>
    </Container>
  );
}
